function [] = compFeatureLength(dirSrc, fileSrc, dirDest, fileDest)% Computes the length of the given song file.%% dirSrc:   absolute or relative path to directory of source audio. Has to%            end with '/' or '\'% fileSrc:  name of source audio file% dirDest:  absolute or relative path to directory of output file. Has to%            end with '/' or '\'% fileDest: name of output file% constantsprecisionOutput = 6; % ouput precision% load song[audio,sideinfo] = wav_to_audio('', dirSrc, fileSrc);Fs = sideinfo.wav.fs;% get lengthlen = length(audio) / Fs;% print length to filefileId = fopen(strcat(dirDest, fileDest), 'w+');format = strcat('%.', int2str(precisionOutput), 'f\r\n');fprintf(fileId, format, len);fclose(fileId);end